import { useState } from "react";
import "../App.css";
import { Link } from "react-router-dom";
import { Button } from "../components/Button";

export default function NftMintingPage() {
  const [isLoading, setIsLoading] = useState(false);
  const [loadedNft, setLoadedNft] = useState();

  function mintNftHandler() {
    setIsLoading(true);

    const url = "http://localhost:8000/nfts/mint/";
    const owner = "1234";

    fetch(url + owner, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setIsLoading(false);
        setLoadedNft(data);
      });
  }

  let content;

  if (isLoading) {
    content = <div className="cards">Minting Your NFT...</div>;
  } else if (loadedNft) {
    content = (
      <div className="cards">
        <h1>
          {" "}
          이름 : {loadedNft.name} / {loadedNft.nameEng}{" "}
        </h1>
        <p>번호 : {loadedNft.sheetId}</p>
        <p>희귀도 : {loadedNft.rarity}</p>
      </div>
    );
  } else {
    content = (
      <div className="cards">
        <div className="cards__item">
          <div className="cards__item__link">
            <figure className="cards__item__pic-wrap" data-category="123">
              <img
                className="cards__item__img"
                alt="Item Image"
                src="https://shop.kosangas.dk/images/products//defaultproduct.gif"
              />
            </figure>
            <div className="cards__item__info">
              <h5 className="cards__item__text">test</h5>
              <p>희귀도 : </p>
              <p>소유자 주소 : </p>
            </div>

            <Button
              className="btns"
              buttonStyle="btn--secondary"
              buttonSize="btn--add"
            >
              Mint
            </Button>
          </div>
        </div>
      </div>
    );
  }
  return (
    <section>
      <div>
        <div>
          <h1>MINT YOUR FURNITURE NOW!</h1>
        </div>

        {content}
      </div>
    </section>
  );
}
