import { useContext } from "react";
import MyNftContext from "../../store/my-nft-context";

import { Link } from "react-router-dom";

function CardItem(props) {
  const myNftCtx = useContext(MyNftContext);
  const itemIsMyNft = myNftCtx.itemIsMyNft(props.id);

  function toggleMyNftStatusHandler() {
    if (itemIsMyNft) {
      myNftCtx.removeMyNft(props.id);
    } else {
      myNftCtx.addMyNft({
        name: props.name,
        nameEng: props.nameEng,
        rarity: props.rarity,
        sheetId: props.id,
      });
    }
  }

  return (
    <div className="cards__item">
      <Link className="cards__item__link" to={props.path}>
        <figure className="cards__item__pic-wrap" data-category={props.label}>
          <img className="cards__item__img" alt="Item Image" src={props.src} />
        </figure>
        <div className="cards__item__info">
          <h5 className="cards__item__text">{props.text}</h5>
          <p>희귀도 : {props.rarity}</p>
          <p>소유자 주소 : {props.owner}</p>
        </div>
      </Link>
    </div>
  );
}

export default CardItem;
